import React, { useState } from "react";

function App() {
  const [page, setPage] = useState("jeux");
  const [search, setSearch] = useState("");

  const jeux = [
    "GTA V",
    "Minecraft",
    "Cyberpunk 2077",
    "Red Dead Redemption 2",
    "Call of Duty",
    "FIFA 24"
  ];

  const logiciels = [
    "Photoshop",
    "Premiere Pro",
    "OBS Studio",
    "Discord",
    "Visual Studio Code",
    "Steam"
  ];

  const data =
    page === "jeux"
      ? jeux.filter(j =>
          j.toLowerCase().includes(search.toLowerCase())
        )
      : logiciels;

  const styles = {
    body: {
      height: "100vh",
      margin: 0,
      background: "radial-gradient(circle at top, #020617, #020617, #0f172a)",
      fontFamily: "Segoe UI, Arial",
      color: "#e5e7eb"
    },
    header: {
      height: "80px",
      display: "flex",
      justifyContent: "flex-end",
      alignItems: "center",
      padding: "0 50px",
      borderBottom: "3px solid #22c55e"
    },
    logo: {
      fontSize: "28px",
      fontWeight: "bold",
      color: "#22c55e",
      letterSpacing: "2px"
    },
    container: {
      display: "flex",
      height: "calc(100vh - 80px)"
    },
    sidebar: {
      width: "260px",
      borderRight: "3px solid #22c55e",
      padding: "30px 20px"
    },
    menuBtn: {
      width: "100%",
      padding: "16px",
      marginBottom: "18px",
      background: "transparent",
      border: "2px solid #22c55e",
      color: "#22c55e",
      fontSize: "16px",
      cursor: "pointer"
    },
    content: {
      flex: 1,
      padding: "40px",
      overflowY: "auto"
    },
    title: {
      fontSize: "26px",
      marginBottom: "25px",
      color: "#22c55e"
    },
    search: {
      width: "340px",
      padding: "14px",
      background: "#020617",
      border: "2px solid #22c55e",
      color: "white",
      fontSize: "15px",
      marginBottom: "30px"
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
      gap: "25px"
    },
    card: {
      background: "linear-gradient(145deg, #020617, #0f172a)",
      border: "2px solid #22c55e",
      borderRadius: "10px",
      padding: "22px"
    },
    cardTitle: {
      marginBottom: "15px",
      fontSize: "18px"
    },
    downloadBtn: {
      width: "100%",
      padding: "12px",
      background: "#22c55e",
      border: "none",
      fontWeight: "bold",
      cursor: "pointer"
    }
  };

  return (
    <div style={styles.body}>
      <div style={styles.header}>
        <div style={styles.logo}>DOWNLOADER X</div>
      </div>

      <div style={styles.container}>
        <div style={styles.sidebar}>
          <button style={styles.menuBtn} onClick={() => setPage("jeux")}>
            ðŸŽ® JEUX
          </button>
          <button style={styles.menuBtn} onClick={() => setPage("logiciels")}>
            ðŸ’» LOGICIELS
          </button>
        </div>

        <div style={styles.content}>
          <div style={styles.title}>
            {page === "jeux" ? "JEUX DISPONIBLES" : "LOGICIELS DISPONIBLES"}
          </div>

          {page === "jeux" && (
            <input
              style={styles.search}
              placeholder="Rechercher un jeu..."
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
          )}

          <div style={styles.grid}>
            {data.map((item, i) => (
              <div style={styles.card} key={i}>
                <div style={styles.cardTitle}>{item}</div>
                <button style={styles.downloadBtn}>
                  TÃ‰LÃ‰CHARGER
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
