import React, { useEffect, useState } from "react";

const API = "http://localhost:5000";

export default function App() {
  const [page, setPage] = useState("jeux");
  const [list, setList] = useState([]);
  const [search, setSearch] = useState("");
  const [status, setStatus] = useState("OFFLINE");

  // üîÑ statut serveur en temps r√©el
  useEffect(() => {
    const ping = () => {
      fetch(`${API}/api/status`)
        .then((r) => r.json())
        .then((j) => setStatus(j.status))
        .catch(() => setStatus("OFFLINE"));
    };
    ping();
    const i = setInterval(ping, 2000);
    return () => clearInterval(i);
  }, []);

  // üì¶ charger jeux / logiciels
  useEffect(() => {
    fetch(`${API}/api/${page}`)
      .then((r) => r.json())
      .then(setList);
  }, [page]);

  const download = (name) => {
    window.location.href = `${API}/download/${page}/${encodeURIComponent(
      name
    )}.zip`;
  };

  const filtered = list.filter((n) =>
    n.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={styles.app}>
      {/* HEADER */}
      <header style={styles.header}>
        <div style={styles.logo}>Downloader</div>

        <div style={styles.nav}>
          <button
            style={styles.navBtn(page === "jeux")}
            onClick={() => setPage("jeux")}
          >
            Jeux
          </button>
          <button
            style={styles.navBtn(page === "logiciels")}
            onClick={() => setPage("logiciels")}
          >
            Logiciels
          </button>
        </div>

        <div style={styles.status(status === "ONLINE")}>
          ‚óè {status}
        </div>
      </header>

      {/* SEARCH */}
      <div style={styles.searchBox}>
        <input
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder={`Rechercher un ${page}...`}
          style={styles.searchInput}
        />
      </div>

      {/* GRID */}
      <div style={styles.grid}>
        {filtered.map((name) => (
          <div key={name} style={styles.card}>
            <img
              src={`${API}/images/${page}/${encodeURIComponent(name)}`}
              alt={name}
              style={styles.cardImg}
              onError={(e) => {
                e.target.src =
                  "https://picsum.photos/400/200?random=" +
                  Math.floor(Math.random() * 1000);
              }}
            />

            <div style={styles.cardBody}>
              <div style={styles.title}>{name}</div>
              <button
                style={styles.downloadBtn}
                onClick={() => download(name)}
              >
                T√©l√©charger
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  app: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #0a0f1e, #05070d)",
    color: "#e6e6e6",
    fontFamily: "'Inter', 'Segoe UI', sans-serif",
  },

  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "22px 48px",
    background: "rgba(10,15,30,0.85)",
    backdropFilter: "blur(12px)",
    borderBottom: "1px solid rgba(255,255,255,0.08)",
    position: "sticky",
    top: 0,
    zIndex: 10,
  },

  logo: {
    fontSize: 24,
    fontWeight: 700,
    letterSpacing: 1,
    color: "#ffffff",
  },

  nav: {
    display: "flex",
    gap: 14,
  },

  navBtn: (active) => ({
    padding: "10px 22px",
    borderRadius: 12,
    fontSize: 14,
    fontWeight: 500,
    cursor: "pointer",
    background: active
      ? "linear-gradient(135deg, #4f9cff, #6b7cff)"
      : "transparent",
    color: active ? "#fff" : "#cfd6ff",
    border: "1px solid rgba(255,255,255,0.12)",
    transition: "all 0.25s ease",
  }),

  status: (online) => ({
    fontSize: 13,
    fontWeight: 600,
    color: online ? "#4cffb0" : "#ff6b6b",
    letterSpacing: 0.5,
  }),

  searchBox: {
    padding: "28px 40px 10px",
    display: "flex",
    justifyContent: "center",
  },

  searchInput: {
    width: "520px",
    maxWidth: "90%",
    padding: "14px 20px",
    borderRadius: 14,
    border: "1px solid rgba(255,255,255,0.12)",
    outline: "none",
    fontSize: 15,
    background: "rgba(255,255,255,0.04)",
    color: "#fff",
    transition: "0.25s",
  },

  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
    gap: 28,
    padding: "30px 40px 60px",
  },

  card: {
    background:
      "linear-gradient(180deg, rgba(255,255,255,0.06), rgba(255,255,255,0.02))",
    borderRadius: 18,
    overflow: "hidden",
    border: "1px solid rgba(255,255,255,0.08)",
    transition: "transform 0.25s ease, box-shadow 0.25s ease",
  },

  cardImg: {
    width: "100%",
    height: 160,
    objectFit: "cover",
  },

  cardBody: {
    padding: 16,
  },

  title: {
    fontSize: 15,
    fontWeight: 600,
    marginBottom: 14,
    color: "#ffffff",
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
  },

  downloadBtn: {
    width: "100%",
    padding: "11px",
    borderRadius: 14,
    background: "linear-gradient(135deg, #4f9cff, #6b7cff)",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    fontWeight: 600,
    fontSize: 14,
    transition: "transform 0.2s ease, box-shadow 0.2s ease",
  },
};
