import React, { useEffect, useState } from "react";

const API = "http://localhost:5000";

export default function App() {
  const [page, setPage] = useState("jeux");
  const [list, setList] = useState([]);
  const [search, setSearch] = useState("");
  const [status, setStatus] = useState("OFFLINE");

  useEffect(() => {
    const ping = () => {
      fetch(`${API}/api/status`)
        .then((r) => r.json())
        .then((j) => setStatus(j.status))
        .catch(() => setStatus("OFFLINE"));
    };
    ping();
    const i = setInterval(ping, 2000);
    return () => clearInterval(i);
  }, []);

  useEffect(() => {
    fetch(`${API}/api/${page}`)
      .then((r) => r.json())
      .then(setList);
  }, [page]);

  const download = (name) => {
    window.location.href = `${API}/download/${page}/${encodeURIComponent(
      name
    )}.zip`;
  };

  const filtered = list.filter((n) =>
    n.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <>
      <style>{css}</style>

      <div className="app">
        {/* HEADER */}
        <header className="header">
          <div className="logo">üéÆ DOWNLOADER</div>

          <div className="nav">
            <button
              className={page === "jeux" ? "navBtn active" : "navBtn"}
              onClick={() => setPage("jeux")}
            >
              Jeux
            </button>
            <button
              className={page === "logiciels" ? "navBtn active" : "navBtn"}
              onClick={() => setPage("logiciels")}
            >
              Logiciels
            </button>
          </div>

          <div className={status === "ONLINE" ? "status online" : "status offline"}>
            ‚óè {status}
          </div>
        </header>

        {/* SEARCH */}
        <div className="searchBox">
          <input
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder={`Rechercher un ${page}...`}
          />
        </div>

        {/* GRID */}
        <div className="grid">
          {filtered.map((name, i) => (
            <div
              className="card"
              key={name}
              style={{ animationDelay: `${i * 0.05}s` }}
            >
              <img
                src={`${API}/images/${page}/${encodeURIComponent(name)}`}
                onError={(e) => {
                  e.target.src =
                    "https://picsum.photos/600/300?random=" +
                    Math.floor(Math.random() * 1000);
                }}
                alt={name}
              />

              <div className="cardBody">
                <div className="title">{name}</div>
                <button onClick={() => download(name)}>
                  T√©l√©charger
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

