import React, { useEffect, useState } from "react";

function App() {
  const [page, setPage] = useState("jeux");
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/${page}`)
      .then(res => res.json())
      .then(json => setData(json))
      .catch(err => console.error(err));
  }, [page]);

  // Remove .zip
  const filtered = data
    .map(item => item.replace(".zip", ""))
    .filter(item => item.toLowerCase().includes(search.toLowerCase()));

  const styles = {
    body: {
      height: "100vh",
      margin: 0,
      background:
        "radial-gradient(circle at top, #050A1A, #050A1A, #0B1026)",
      fontFamily: "Segoe UI, Arial",
      color: "#E5E7EB"
    },
    header: {
      height: "80px",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "0 40px",
      borderBottom: "2px solid #22c55e"
    },
    logo: {
      fontSize: "28px",
      fontWeight: "bold",
      color: "#22c55e",
      letterSpacing: "2px"
    },
    menu: {
      display: "flex",
      gap: "12px"
    },
    menuBtn: {
      padding: "12px 18px",
      background: "transparent",
      border: "2px solid #22c55e",
      color: "#22c55e",
      cursor: "pointer",
      borderRadius: "8px",
      fontWeight: "bold"
    },
    menuBtnActive: {
      background: "#22c55e",
      color: "#0B1026"
    },
    container: {
      display: "flex",
      height: "calc(100vh - 80px)"
    },
    sidebar: {
      width: "280px",
      borderRight: "2px solid #22c55e",
      padding: "30px 20px"
    },
    title: {
      fontSize: "26px",
      marginBottom: "15px",
      color: "#22c55e"
    },
    search: {
      width: "100%",
      padding: "14px",
      background: "#0B1026",
      border: "2px solid #22c55e",
      color: "white",
      fontSize: "15px",
      marginBottom: "25px",
      borderRadius: "10px"
    },
    content: {
      flex: 1,
      padding: "40px",
      overflowY: "auto"
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(240px, 1fr))",
      gap: "25px"
    },
    card: {
      background: "linear-gradient(145deg, #0B1026, #050A1A)",
      border: "2px solid #22c55e",
      borderRadius: "14px",
      padding: "18px",
      transition: "0.2s",
      boxShadow: "0 0 20px rgba(34, 197, 94, 0.25)"
    },
    cardHover: {
      transform: "scale(1.04)"
    },
    cardImg: {
      width: "100%",
      height: "140px",
      background:
        "linear-gradient(135deg, rgba(34,197,94,0.4), rgba(34,197,94,0))",
      borderRadius: "10px",
      marginBottom: "15px"
    },
    cardTitle: {
      marginBottom: "10px",
      fontSize: "18px",
      fontWeight: "bold"
    },
    downloadBtn: {
      width: "100%",
      padding: "12px",
      background: "#22c55e",
      border: "none",
      fontWeight: "bold",
      cursor: "pointer",
      borderRadius: "10px"
    }
  };

  return (
    <div style={styles.body}>
      <div style={styles.header}>
        <div style={styles.logo}>DOWNLOADER X</div>

        <div style={styles.menu}>
          <button
            style={{
              ...styles.menuBtn,
              ...(page === "jeux" ? styles.menuBtnActive : {})
            }}
            onClick={() => setPage("jeux")}
          >
            ðŸŽ® JEUX
          </button>

          <button
            style={{
              ...styles.menuBtn,
              ...(page === "logiciels" ? styles.menuBtnActive : {})
            }}
            onClick={() => setPage("logiciels")}
          >
            ðŸ’» LOGICIELS
          </button>
        </div>
      </div>

      <div style={styles.container}>
        <div style={styles.sidebar}>
          <div style={styles.title}>
            {page === "jeux" ? "JEUX" : "LOGICIELS"}
          </div>

          <input
            style={styles.search}
            placeholder="Recherche..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          <div style={{ color: "#A3A3A3", marginTop: "20px" }}>
            <div>Serveur : <span style={{ color: "#22c55e" }}>OK</span></div>
            <div>Port : <span style={{ color: "#22c55e" }}>5000</span></div>
          </div>
        </div>

        <div style={styles.content}>
          <div style={styles.grid}>
            {filtered.map((item, i) => (
              <div
                style={styles.card}
                key={i}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "scale(1.04)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "scale(1)";
                }}
              >
                <div style={styles.cardImg} />

                <div style={styles.cardTitle}>{item}</div>
                <button style={styles.downloadBtn}>TÃ‰LÃ‰CHARGER</button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
