import React, { useEffect, useState } from "react";

const API = "http://localhost:5000";

function App() {
  const [page, setPage] = useState("jeux");
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [status, setStatus] = useState("...");

  useEffect(() => {
    const ping = () => {
      fetch(`${API}/api/status`)
        .then((r) => r.json())
        .then((j) => setStatus(j.status))
        .catch(() => setStatus("OFFLINE"));
    };
    ping();
    const interval = setInterval(ping, 2500);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    fetch(`${API}/api/${page}`)
      .then((r) => r.json())
      .then((j) => setData(j))
      .catch(() => setData([]));
  }, [page]);

  const filtered = data.filter((x) =>
    x.toLowerCase().includes(search.toLowerCase())
  );

  const statusColor = status === "ONLINE" ? "#22c55e" : "#ff2e2e";

  return (
    <div style={styles.page}>
      <div style={styles.header}>
        <div style={styles.logo}>DOWNLOADER X</div>

        <div style={styles.statusBox(statusColor)}>
          <div style={styles.statusDot(statusColor)} />
          <div style={styles.statusText}>Serveur</div>
          <div style={styles.statusValue}>{status}</div>
        </div>
      </div>

      <div style={styles.main}>
        <div style={styles.sidebar}>
          <div style={styles.searchBox}>
            <input
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Rechercher un jeu ou logiciel..."
              style={styles.searchInput}
            />
          </div>

          <button
            onClick={() => setPage("jeux")}
            style={page === "jeux" ? styles.btnActive : styles.btn}
          >
            ðŸŽ® Jeux
          </button>

          <button
            onClick={() => setPage("logiciels")}
            style={page === "logiciels" ? styles.btnActive : styles.btn}
          >
            ðŸ’» Logiciels
          </button>
        </div>

        <div style={styles.content}>
          <div style={styles.title}>
            <div style={styles.titleText}>
              {page === "jeux" ? "Jeux disponibles" : "Logiciels disponibles"}
            </div>
            <div style={styles.subtitle}>
              SÃ©lectionne un item et clique sur tÃ©lÃ©charger
            </div>
          </div>

          <div style={styles.grid}>
            {filtered.map((name, i) => (
              <div key={i} style={styles.card}>
                <img
                  src={`${API}/images/${page}/${encodeURIComponent(name)}`}
                  alt={name}
                  style={styles.cardImg}
                  onError={(e) => {
                    e.target.src =
                      "https://picsum.photos/400/200?random=" + i;
                  }}
                />

                <div style={styles.cardBody}>
                  <div style={styles.cardTitle}>{name}</div>

                  <button
                    onClick={() =>
                      (window.location.href = `${API}/download/${page}/${encodeURIComponent(
                        name
                      )}`)
                    }
                    style={styles.downloadBtn}
                  >
                    TÃ©lÃ©charger
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    height: "100vh",
    margin: 0,
    fontFamily: "Segoe UI",
    background:
      "radial-gradient(circle at top left, #1b1b2f, #05060d 60%, #04050b 100%)",
    color: "#e5e7eb",
    overflow: "hidden",
  },

  header: {
    height: 70,
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0 40px",
    borderBottom: "1px solid rgba(255,255,255,0.1)",
  },

  logo: {
    fontSize: 26,
    fontWeight: "bold",
    letterSpacing: 2,
    color: "#22c55e",
    textShadow: "0 0 10px rgba(34,197,94,0.6)",
  },

  statusBox: (color) => ({
    display: "flex",
    alignItems: "center",
    gap: 10,
    padding: "10px 16px",
    borderRadius: 14,
    background: "rgba(255,255,255,0.06)",
    border: `1px solid ${color}`,
  }),

  statusDot: (color) => ({
    width: 10,
    height: 10,
    borderRadius: 999,
    background: color,
    boxShadow: `0 0 10px ${color}`,
  }),

  statusText: {
    fontWeight: "bold",
    letterSpacing: 1,
  },

  statusValue: {
    fontSize: 12,
    color: "rgba(255,255,255,0.7)",
  },

  main: {
    display: "flex",
    height: "calc(100vh - 70px)",
  },

  sidebar: {
    width: 360,
    borderRight: "1px solid rgba(255,255,255,0.08)",
    padding: 30,
    display: "flex",
    flexDirection: "column",
    gap: 18,
  },

  searchBox: {
    padding: 10,
    borderRadius: 16,
    background: "rgba(255,255,255,0.04)",
    border: "1px solid rgba(255,255,255,0.15)",
  },

  searchInput: {
    width: "100%",
    padding: 16,
    borderRadius: 12,
    border: "none",
    background: "transparent",
    color: "#fff",
    outline: "none",
    fontSize: 16,
  },

  btn: {
    padding: 14,
    borderRadius: 14,
    border: "1px solid rgba(255,255,255,0.15)",
    background: "transparent",
    color: "#e5e7eb",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.2s",
  },

  btnActive: {
    padding: 14,
    borderRadius: 14,
    border: "1px solid #22c55e",
    background: "rgba(34,197,94,0.18)",
    color: "#e5e7eb",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.2s",
  },

  content: {
    flex: 1,
    padding: 30,
    overflowY: "auto",
  },

  title: {
    marginBottom: 20,
  },

  titleText: {
    fontSize: 22,
    fontWeight: "bold",
    letterSpacing: 1,
  },

  subtitle: {
    color: "rgba(255,255,255,0.7)",
    marginTop: 6,
  },

  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
    gap: 25,
  },

  card: {
    background: "rgba(255,255,255,0.04)",
    border: "1px solid rgba(255,255,255,0.12)",
    borderRadius: 18,
    overflow: "hidden",
    boxShadow: "0 20px 50px rgba(0,0,0,0.6)",
    transition: "0.2s",
  },

  cardImg: {
    width: "100%",
    height: 160,
    objectFit: "cover",
  },

  cardBody: {
    padding: 18,
  },

  cardTitle: {
    fontWeight: "bold",
    fontSize: 18,
    marginBottom: 12,
  },

  downloadBtn: {
    width: "100%",
    padding: 12,
    borderRadius: 14,
    border: "none",
    background: "#22c55e",
    color: "#0b0f1f",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.2s",
  },
};

export default App;
