import React, { useEffect, useState } from "react";

function App() {
  const [page, setPage] = useState("jeux");
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/${page}`)
      .then((res) => res.json())
      .then((json) => setData(json))
      .catch((err) => console.error(err));
  }, [page]);

  const filtered = data
    .map((item) => item.replace(".zip", ""))
    .filter((item) => item.toLowerCase().includes(search.toLowerCase()));

  const styles = {
    body: {
      height: "100vh",
      margin: 0,
      background:
        "linear-gradient(180deg, #050816 0%, #050816 45%, #0B1026 100%)",
      fontFamily: "Segoe UI, Arial",
      color: "#E5E7EB",
      overflow: "hidden",
    },

    header: {
      height: "70px",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "0 40px",
      borderBottom: "1px solid rgba(34, 197, 94, 0.35)",
    },

    logo: {
      fontSize: "26px",
      fontWeight: "bold",
      color: "#22c55e",
      letterSpacing: "2px",
    },

    serverBox: {
      display: "flex",
      gap: "12px",
      alignItems: "center",
      padding: "10px 18px",
      borderRadius: "12px",
      background: "rgba(255,255,255,0.05)",
      border: "1px solid rgba(34,197,94,0.35)",
      boxShadow: "0 10px 25px rgba(0,0,0,0.4)",
    },

    serverDot: {
      width: "10px",
      height: "10px",
      borderRadius: "50%",
      background: "#22c55e",
    },

    container: {
      display: "flex",
      height: "calc(100vh - 70px)",
    },

    sidebar: {
      width: "300px",
      borderRight: "1px solid rgba(34, 197, 94, 0.2)",
      padding: "30px 20px",
      display: "flex",
      flexDirection: "column",
      gap: "20px",
    },

    menuTitle: {
      fontSize: "18px",
      color: "rgba(255,255,255,0.8)",
    },

    menuBtn: {
      padding: "14px 18px",
      background: "transparent",
      border: "1px solid rgba(255,255,255,0.15)",
      color: "#E5E7EB",
      cursor: "pointer",
      borderRadius: "12px",
      fontWeight: "bold",
      textAlign: "left",
      transition: "0.2s",
      display: "flex",
      alignItems: "center",
      gap: "10px",
    },

    menuBtnActive: {
      border: "1px solid rgba(34, 197, 94, 0.7)",
      background: "rgba(34, 197, 94, 0.12)",
    },

    search: {
      width: "100%",
      padding: "14px",
      background: "rgba(255,255,255,0.05)",
      border: "1px solid rgba(255,255,255,0.15)",
      color: "white",
      fontSize: "15px",
      borderRadius: "12px",
      outline: "none",
    },

    content: {
      flex: 1,
      padding: "30px",
      overflowY: "auto",
    },

    grid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(240px, 1fr))",
      gap: "25px",
    },

    card: {
      background: "rgba(255,255,255,0.04)",
      border: "1px solid rgba(255,255,255,0.15)",
      borderRadius: "16px",
      overflow: "hidden",
      boxShadow: "0 20px 40px rgba(0,0,0,0.5)",
      transition: "transform 0.2s, border 0.2s",
      cursor: "pointer",
    },

    cardHover: {
      transform: "translateY(-6px)",
      border: "1px solid rgba(34,197,94,0.6)",
    },

    cardImg: {
      width: "100%",
      height: "150px",
      objectFit: "cover",
    },

    cardBody: {
      padding: "18px",
    },

    cardTitle: {
      fontSize: "18px",
      fontWeight: "bold",
      marginBottom: "10px",
    },

    downloadBtn: {
      width: "100%",
      padding: "12px",
      background: "#22c55e",
      border: "none",
      fontWeight: "bold",
      cursor: "pointer",
      borderRadius: "12px",
      marginTop: "10px",
    },
  };

  return (
    <div style={styles.body}>
      <div style={styles.header}>
        <div style={styles.logo}>DOWNLOADER X</div>

        <div style={styles.serverBox}>
          <div style={styles.serverDot}></div>
          <div>
            <div style={{ fontWeight: "bold" }}>Serveur</div>
            <div style={{ fontSize: "12px", color: "rgba(255,255,255,0.7)" }}>
              CONNECTÃ‰ (5000)
            </div>
          </div>
        </div>
      </div>

      <div style={styles.container}>
        <div style={styles.sidebar}>
          <div style={styles.menuTitle}>CATÃ‰GORIES</div>

          <button
            style={{
              ...styles.menuBtn,
              ...(page === "jeux" ? styles.menuBtnActive : {}),
            }}
            onClick={() => setPage("jeux")}
          >
            ðŸŽ® JEUX
          </button>

          <button
            style={{
              ...styles.menuBtn,
              ...(page === "logiciels" ? styles.menuBtnActive : {}),
            }}
            onClick={() => setPage("logiciels")}
          >
            ðŸ’» LOGICIELS
          </button>

          <input
            style={styles.search}
            placeholder="Rechercher..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        <div style={styles.content}>
          <div style={styles.grid}>
            {filtered.map((item, i) => (
              <div
                style={styles.card}
                key={i}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "translateY(-6px)";
                  e.currentTarget.style.border =
                    "1px solid rgba(34,197,94,0.6)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "translateY(0px)";
                  e.currentTarget.style.border =
                    "1px solid rgba(255,255,255,0.15)";
                }}
              >
                <img
                  style={styles.cardImg}
                  src={`https://picsum.photos/seed/${encodeURIComponent(
                    item
                  )}/400/200`}
                  alt={item}
                />

                <div style={styles.cardBody}>
                  <div style={styles.cardTitle}>{item}</div>
                  <button style={styles.downloadBtn}>TÃ‰LÃ‰CHARGER</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
