import React, { useEffect, useState } from "react";

function App() {
  const [page, setPage] = useState("jeux");
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [status, setStatus] = useState("...");

  useEffect(() => {
    const ping = () => {
      fetch("http://localhost:5000/api/status")
        .then((r) => r.json())
        .then((j) => setStatus(j.status))
        .catch(() => setStatus("OFFLINE"));
    };
    ping();
    const interval = setInterval(ping, 3000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    fetch(`http://localhost:5000/api/${page}`)
      .then((r) => r.json())
      .then((j) => setData(j))
      .catch(() => setData([]));
  }, [page]);

  const filtered = data.filter((x) =>
    x.toLowerCase().includes(search.toLowerCase())
  );

  const green = status === "ONLINE" ? "#22c55e" : "#ff2e2e";

  return (
    <div style={{
      height: "100vh",
      margin: 0,
      fontFamily: "Segoe UI",
      background: "linear-gradient(180deg, #04050b, #0b0f1f)",
      color: "#e5e7eb",
      overflow: "hidden"
    }}>
      <div style={{
        height: 70,
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "0 40px",
        borderBottom: "1px solid rgba(34,197,94,0.25)"
      }}>
        <div style={{ fontSize: 26, fontWeight: "bold", color: "#22c55e" }}>
          DOWNLOADER X
        </div>

        <div style={{
          display: "flex",
          alignItems: "center",
          gap: 10,
          padding: "10px 16px",
          borderRadius: 12,
          background: "rgba(255,255,255,0.05)",
          border: `1px solid ${green}`
        }}>
          <div style={{
            width: 10, height: 10, borderRadius: 999,
            background: green
          }} />
          <div style={{ fontWeight: "bold" }}>Serveur</div>
          <div style={{ fontSize: 12, color: "rgba(255,255,255,0.7)" }}>
            {status}
          </div>
        </div>
      </div>

      <div style={{ display: "flex", height: "calc(100vh - 70px)" }}>
        <div style={{
          width: 340,
          borderRight: "1px solid rgba(34,197,94,0.15)",
          padding: 30,
          display: "flex",
          flexDirection: "column",
          gap: 18
        }}>
          <input
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder="Rechercher un jeu ou un logiciel..."
            style={{
              padding: 16,
              borderRadius: 14,
              border: "1px solid rgba(255,255,255,0.15)",
              background: "rgba(255,255,255,0.05)",
              color: "#fff",
              outline: "none",
              fontSize: 16
            }}
          />

          <button
            onClick={() => setPage("jeux")}
            style={{
              padding: 14,
              borderRadius: 12,
              border: page === "jeux" ? "1px solid #22c55e" : "1px solid rgba(255,255,255,0.15)",
              background: page === "jeux" ? "rgba(34,197,94,0.15)" : "transparent",
              color: "#e5e7eb",
              fontWeight: "bold",
              cursor: "pointer"
            }}
          >
            ðŸŽ® JEUX
          </button>

          <button
            onClick={() => setPage("logiciels")}
            style={{
              padding: 14,
              borderRadius: 12,
              border: page === "logiciels" ? "1px solid #22c55e" : "1px solid rgba(255,255,255,0.15)",
              background: page === "logiciels" ? "rgba(34,197,94,0.15)" : "transparent",
              color: "#e5e7eb",
              fontWeight: "bold",
              cursor: "pointer"
            }}
          >
            ðŸ’» LOGICIELS
          </button>
        </div>

        <div style={{ flex: 1, padding: 30, overflowY: "auto" }}>
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
            gap: 25
          }}>
            {filtered.map((name, i) => (
              <div
                key={i}
                style={{
                  background: "rgba(255,255,255,0.04)",
                  border: "1px solid rgba(255,255,255,0.15)",
                  borderRadius: 16,
                  overflow: "hidden",
                  boxShadow: "0 20px 40px rgba(0,0,0,0.5)",
                  cursor: "pointer"
                }}
              >
                <img
                  src={`http://localhost:5000/images/${page}/${encodeURIComponent(name)}`}
                  alt={name}
                  style={{ width: "100%", height: 150, objectFit: "cover" }}
                  onError={(e) => {
                    e.target.src = "https://picsum.photos/400/200?random=" + i;
                  }}
                />

                <div style={{ padding: 18 }}>
                  <div style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>
                    {name}
                  </div>

                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      window.location.href = `http://localhost:5000/download/${page}/${encodeURIComponent(name)}`;
                    }}
                    style={{
                      width: "100%",
                      padding: 12,
                      borderRadius: 12,
                      border: "none",
                      background: "#22c55e",
                      color: "#0b0f1f",
                      fontWeight: "bold",
                      cursor: "pointer"
                    }}
                  >
                    TÃ©lÃ©charger
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
